import axios from 'axios';

const apiUrl = "http://localhost:5122/items"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}`)    
    return result.data;
  },

  addTask: async(name)=>{
    console.log('addTask', name)
    const result = await axios.post(apiUrl, {name:name, isComplete:false});///???, id:4
    return result.data;
  },

  setCompleted: async(id, isComplete)=>{
    console.log('setCompleted', {id,name, isComplete})
    const result = await axios.put(`${apiUrl}/${id}`, {Name:name, IsComplete:isComplete})//???????
    return result.data;
  },

  deleteTask:async(id)=>{
    const result = await axios.delete(`${apiUrl}/${id}`)///????
    console.log('deleteTask')
    return result.data;
  }
};
